// UnFederalReserveToken with Governance.
contract UnFederalReserveToken is ERC20("UnFederalReserveToken", "eRSDL"), Ownable {
    /// @notice Creates `_amount` token to `_to`. Must only be called by the owner (MasterUnChairman).
    function mint(address _to, uint256 _amount) public onlyOwner {
        _mint(_to, _amount);
        _moveDelegates(address(0), _delegates[_to], _amount);
