contract Toasters is ERC721, Ownable {
    using SafeMath for uint256;

    uint256 public constant mintPrice = 30000000000000000; // 0.03 ETH
    // uint256 public constant mintPrice = 300000000000000;
    uint256 public constant maxPurchase = 20;
    uint256 public MAX_SUPPLY = 7777;
    bool public saleIsActive = false;

    constructor() ERC721("Toaster", "TST") {}

    function withdraw() public onlyOwner {
        uint256 balance = address(this).balance;
        msg.sender.transfer(balance);
    }

