// File: ApePunks.sol

pragma solidity ^0.8.0;

/// @author Hammad Ghazi
contract ApePunks is ERC721Enumerable, Ownable {
    using Counters for Counters.Counter;
    Counters.Counter private _tokenId;

    uint256 public constant MAX_PUNKS = 3333;
    uint256 public price = 30000000000000000; //0.03 Ether
    string baseTokenURI;
    bool public saleOpen = false;

    event ApePunksMinted(uint256 totalMinted);

    constructor(string memory baseURI) ERC721("ApePunks", "ApePunks") {
        setBaseURI(baseURI);
    }

    //Get token Ids of all tokens owned by _owner
    function walletOfOwner(address _owner)
        external
        view
        returns (uint256[] memory)
    {
        uint256 tokenCount = balanceOf(_owner);

        uint256[] memory tokensId = new uint256[](tokenCount);
        for (uint256 i = 0; i < tokenCount; i++) {
            tokensId[i] = tokenOfOwnerByIndex(_owner, i);
        }

        return tokensId;
    }

    function setBaseURI(string memory baseURI) public onlyOwner {
        baseTokenURI = baseURI;
    }

    function setPrice(uint256 _newPrice) public onlyOwner {
        price = _newPrice;
    }

    //Close sale if open, open sale if closed
    function flipSaleState() public onlyOwner {
        saleOpen = !saleOpen;
    }

    function withdrawAll() public onlyOwner {
        (bool success, ) = msg.sender.call{value: address(this).balance}("");
        require(success, "Transfer failed.");
    }

    function mintByAdmin(address _to, uint256 _count) public onlyOwner {
        require(
            _count > 0 && _count <= 20,
            "Min 1 & Max 20 ApePunks can be minted per transaction"
        );
        require(
            totalSupply() + _count <= MAX_PUNKS,
            "Transaction will exceed maximum supply of ApePunks"
        );

        for (uint256 i = 0; i < _count; i++) {
            _mint(_to);
        }
    }

    //mint ApePunks
    function mintApePunks(uint256 _count) public payable {
        require(saleOpen, "Sale is not open yet");
        require(
            _count > 0 && _count <= 20,
            "Min 1 & Max 20 ApePunks can be minted per transaction"
        );
        require(
            totalSupply() + _count <= MAX_PUNKS,
            "Transaction will exceed maximum supply of ApePunks"
        );
        require(
            msg.value >= price * _count,
            "Ether sent with this transaction is not correct"
        );

        address _to = msg.sender;

        for (uint256 i = 0; i < _count; i++) {
            _mint(_to);
        }
    }

    function _mint(address _to) private {
        _tokenId.increment();
        uint256 tokenId = _tokenId.current();
        _safeMint(_to, tokenId);
        emit ApePunksMinted(tokenId);
    }

    function _baseURI() internal view virtual override returns (string memory) {
        return baseTokenURI;
    }
}

