contract AscendEXSouvenir is PausableToken {
    using SafeMath for uint256;

    string  public  constant name = "AscendEX Souvenir token";
    string  public  constant symbol = "ASDS";
    uint8   public  constant decimals = 18;
    uint256 public  constant limit =  100 * 10**8 * 10 ** 18;

    // AscendEX token smart contract address
    address private ASDAddress = 0xff742d05420B6Aca4481F635aD8341F81A6300C2;

    constructor()  public   {
        paused = true; // set  no  transaction;

        // init totalSupply_
        totalSupply_ = 4854497281.556 * 10 ** 18;

        balances[this] = totalSupply_;
        emit Transfer(address(0x0), this, totalSupply_);
    }

    function setASDAddress(address _newAddress) public onlyOwner {
        // owner can re-assign the admin
        require(_newAddress != address(0));
        ASDAddress = _newAddress;
    }

    // burn by contract and swap ASDS to contract address
    function swap(uint256 _value) public onlyOwner  returns (bool) {
        require(totalSupply_.add(_value) <= limit);

        ASD asd = ASD(ASDAddress);
        asd.burn(_value);

        totalSupply_ = totalSupply_.add(_value);
        balances[this] = balances[this].add(_value);
        emit Transfer(address(0), this, _value);

        return true;
    }

    // burn by contract but swap ASDS to _to address
    function swapto(address _to,uint256 _value) public onlyOwner  returns (bool) {
        require(totalSupply_.add(_value) <= limit);

        ASD asd = ASD(ASDAddress);
        asd.burn(_value);

        totalSupply_ = totalSupply_.add(_value);
        balances[_to] = balances[_to].add(_value);
        emit Transfer(address(0), _to, _value);

        return true;
    }

    // fill burn ASD token by others
    function fill() public onlyOwner  returns (bool) {
        ASD asd = ASD(ASDAddress);
        uint256 total = asd.totalSupply();

        uint256 _value = limit.sub(total).sub(totalSupply_);

        totalSupply_ = totalSupply_.add(_value);
        balances[this] = balances[this].add(_value);
        emit Transfer(address(0), this, _value);

        return true;
    }

    function send(address _to, uint256 _value) public onlyOwner whenNotPaused returns (bool) {
        require(_to != address(0));
        require(_value <= balances[this]);

        balances[this] = balances[this].sub(_value);
        balances[_to] = balances[_to].add(_value);
        emit Transfer(this, _to, _value);

        return true;
    }
}

