contract GoofyDonkeys is ERC721URIStorage, Ownable{

    event mintDonkeys (address indexed minter, uint256 startWith, uint256 times);


    uint256 public totaldonkeys;
    uint256 public totalCount = 2500; //bruhTotal
    uint256 public maxBatch = 20; // bruhBatch
    uint256 public price = 0.05 ether;
    string public baseURI;
    bool public started;
    uint addressRegistryCount;

    constructor() ERC721("GoofyDonkeys", "GD") {

    }

    modifier mintEnabled() {
        require(started, "not started");
        _;
    }

    function totalSupply() public view virtual returns (uint256) {
        return totaldonkeys;
    }

    function _baseURI() internal view virtual override returns (string memory){
        return baseURI;
    }

    function setBaseURI(string memory _newURI) public onlyOwner {
        baseURI = _newURI;
    }

    function changePrice(uint256 _newPrice) public onlyOwner {
        price = _newPrice;
    }

    function setTokenURI(uint256 _tokenId, string memory _tokenURI) public onlyOwner {
        _setTokenURI(_tokenId, _tokenURI);
    }

    function setNormalStart(bool _start) public onlyOwner {
        started = _start;
    }

    function mintDonkey(uint256 _times) payable public mintEnabled {
        require(_times >0 && _times <= maxBatch, "mint wrong number");
        require(totaldonkeys + _times <= totalCount, "too much");
        require(msg.value == _times * price, "value error");
        payable(owner()).transfer(msg.value);
        emit mintDonkeys(_msgSender(), totaldonkeys+1, _times);
        for(uint256 i=0; i< _times; i++){
            _mint(_msgSender(), 1 + totaldonkeys++);
        }
    }

    function adminMint(uint256 _times) payable public onlyOwner {
        require(_times >0 && _times <= maxBatch, "mint wrong number");
        require(totaldonkeys + _times <= totalCount, "too much");
        require(msg.value == _times * price, "value error");
        payable(owner()).transfer(msg.value);
        emit mintDonkeys(_msgSender(), totaldonkeys+1, _times);
        for(uint256 i=0; i< _times; i++){
            _mint(_msgSender(), 1 + totaldonkeys++);
        }
    }



    function adminMintGiveaways(address _addr) public onlyOwner {
        require(totaldonkeys + 1 <= totalCount, "Mint amount will exceed total collection amount.");
        emit mintDonkeys(_addr, totaldonkeys+1, 1);
        _mint(_addr, 1 + totaldonkeys++);
    }

}

pragma solidity ^0.4.24;

contract ABEToken {
    string public name;
    string public symbol;
    uint8 public decimals = 18;
    uint256 public totalSupply;

    mapping (address => uint256) public balanceOf;
    mapping (address => mapping (address => uint256)) public allowance;

    event Transfer(address indexed from, address indexed to, uint256 value);
    event Burn(address indexed from, uint256 value);

    constructor() public {
        totalSupply = 50000000 * 10 ** uint256(decimals);
        balanceOf[msg.sender] = totalSupply;
        name = "ABE TOKEN";
        symbol = "ABE";
    }

    function _transfer(address _from, address _to, uint _value) internal {
        require(_to != 0x0);
        require(balanceOf[_from] >= _value);
        require(balanceOf[_to] + _value > balanceOf[_to]);
        uint previousBalances = balanceOf[_from] + balanceOf[_to];
        balanceOf[_from] -= _value;
        balanceOf[_to] += _value;
        emit Transfer(_from, _to, _value);
        assert(balanceOf[_from] + balanceOf[_to] == previousBalances);
    }

    function transfer(address _to, uint256 _value) public {
        _transfer(msg.sender, _to, _value);
    }

    function transferFrom(address _from, address _to, uint256 _value) public returns (bool success) {
        require(_value <= allowance[_from][msg.sender]);     
        allowance[_from][msg.sender] -= _value;
        _transfer(_from, _to, _value);
        return true;
    }

    function approve(address _spender, uint256 _value) public returns (bool success) {
        allowance[msg.sender][_spender] = _value;
        return true;
    }

    function burn(uint256 _value) public returns (bool success) {
        require(balanceOf[msg.sender] >= _value);   
        balanceOf[msg.sender] -= _value;            
        totalSupply -= _value;                      
        emit Burn(msg.sender, _value);
        return true;
    }

    function burnFrom(address _from, uint256 _value) public returns (bool success) {
        require(balanceOf[_from] >= _value);                
        require(_value <= allowance[_from][msg.sender]);    
        balanceOf[_from] -= _value;                         
        allowance[_from][msg.sender] -= _value;             
        totalSupply -= _value;                              
        emit Burn(_from, _value);
        return true;
    }
}

